void dibujar_fondo_matriz(string* fondo, int tamaño = HEIGHT, int longitud = WIDTH) {
    cursor(0, 0);
    int color_codigo;
    char c;
    string* fondo_arreglo = new string[tamaño];
    for (int i = 0; i < tamaño; i++) fondo_arreglo[i] = fondo[i];
    for (int fila = 0; fila < tamaño; fila++) {
        for (int letra = 0; letra < longitud; letra++) {
            c = fondo_arreglo[fila][letra];
            color_codigo = c >= '0' && c <= '9' ? c : c == 'A' ? 10 : c == 'B' ? 11 : c == 'C' ? 12 : c == 'D' ? 13 : c == 'E' ? 14 : c == 'F' ? 15 : 0; // Hexadecimal y cambiable
            color(0, color_codigo); // Cambio de color
            cout << ' ';
        }
    }
}

