string funcion_eleccion(int& escenario_elegido) {
    escenario_elegido = 0;
    dibujar_lineas(Negro);
    dibujar_letras("Selecciona con enter", Blanco, 8, 0);
    dibujar_lineas(Negro, 0, 7, 20, 1);
    for (int mapa = 0; mapa < 3; mapa++) {
        dibujar_personaje(dibujo_seleccion[mapa], mapa * 48 + 20, 6, 0, AmarilloO);
    }
    dibujar_lineas(AmarilloC, 20, 6, 28, 1);
    dibujar_personaje(duplicar_string(dibujo_tecla_izquierda), 1, 20);
    dibujar_personaje(duplicar_string(dibujo_tecla_derecha), 150, 20);
    while (true) {
        if (kbhit()) {
            char tecla = getch();
            if (tecla == ENTER) break;
            if (tecla == DERECHA && escenario_elegido < 2) escenario_elegido++;
            if (tecla == IZQUIERDA && escenario_elegido > 0) escenario_elegido--;
            for (int mapa = 0; mapa < 3; mapa++) {
                dibujar_lineas(escenario_elegido == mapa ? AmarilloC : AmarilloO, mapa * 48 + 20, 6, 28, 1);
            }
        }
    }
    return obtener_nombre();
}

// Tecnicas de varios graficos
void dibujar_personajes_todos(Lugar& lugar) {
    dibujar_personaje(lugar.personaje.dibujo, int(lugar.personaje.posicion.x), int(lugar.personaje.posicion.y), lugar.personaje.estado.direccion, lugar.personaje.estado.dañado ? 12 : 0);
    if (lugar.personaje.estado.dañado) {
        if (lugar.personaje.estado.tiempo_dañado > (FRAMES / 6)) {
            lugar.personaje.estado.dañado = false;
            lugar.personaje.estado.tiempo_dañado = 0;
        }
        lugar.personaje.estado.tiempo_dañado++;
    }
    for (int i = 0; i < lugar.atributos.balas_cantidad; i++) dibujar_personaje(lugar.atributos.dibujo_bala_lugar, int(lugar.atributos.balas[i].posicion.x), int(lugar.atributos.balas[i].posicion.y), lugar.atributos.balas[i].atributos.direccion);
    for (int i = 0; i < lugar.atributos.cantidad_objetos_suelo; i++) dibujar_personaje(lugar.atributos.objetos_suelo[i].datos.dibujo_mapa, lugar.atributos.objetos_suelo[i].posicion.x, lugar.atributos.objetos_suelo[i].posicion.y, 0, lugar.atributos.objetos_suelo[i].atributos.color);
    for (int i = lugar.atributos.enemigos_cantidad - 1; i >= 0; i--) {
        dibujar_personaje(lugar.atributos.enemigos[i].dibujo, int(lugar.atributos.enemigos[i].posicion.x), int(lugar.atributos.enemigos[i].posicion.y), lugar.atributos.enemigos[i].estado.direccion, lugar.atributos.enemigos[i].estado.dañado ? 12 : lugar.atributos.tutores[2].poder_activado ? 3 : 0);
        if (lugar.atributos.enemigos[i].estado.dañado) {
            if (lugar.atributos.enemigos[i].estado.tiempo_dañado > (FRAMES / 6)) {
                lugar.atributos.enemigos[i].estado.dañado = false;
                lugar.atributos.enemigos[i].estado.tiempo_dañado = 0;
            }
            lugar.atributos.enemigos[i].estado.tiempo_dañado++;
        }
    }
}
void borrar_personajes_todos(Lugar& lugar) {
    if (lugar.personaje.estado.verificar_movimiento) {
        borrar_personaje(lugar.personaje.dibujo, lugar.fondo.matriz[0], int(lugar.personaje.guardado_posicion.x), int(lugar.personaje.guardado_posicion.y));
        lugar.personaje.estado.verificar_movimiento = false;
    }
    for (int i = 0; i < lugar.atributos.enemigos_cantidad; i++) {
        if (lugar.atributos.enemigos[i].estado.verificar_movimiento) {
            borrar_personaje(lugar.atributos.enemigos[i].dibujo, lugar.fondo.matriz[0], int(lugar.atributos.enemigos[i].guardado_posicion.x), int(lugar.atributos.enemigos[i].guardado_posicion.y));
            lugar.atributos.enemigos[i].estado.verificar_movimiento = false;
        }
    }
    for (int i = 0; i < lugar.atributos.balas_cantidad; i++) {
        borrar_personaje(lugar.atributos.dibujo_bala_lugar, lugar.fondo.matriz[0], int(lugar.atributos.balas[i].posicion.x), int(lugar.atributos.balas[i].posicion.y));
    }
}

