// Movimientos
void movimiento_bala(Bala& bala) {
    float pos_x_guardada = bala.posicion.x;
    if (bala.atributos.direccion == 1) bala.posicion.x -= bala.velocidad.rapidez / FRAMES;
    else bala.posicion.x += bala.velocidad.rapidez / FRAMES;
    bala.guardado_posicion.x = pos_x_guardada;
}
void movimiento_enemigo(Entidad& enemigo, Entidad principal, bool activado) {
    float pos_x_guardada = enemigo.posicion.x, pos_y_guardada = enemigo.posicion.y;
    int guardado_direccion = enemigo.estado.direccion;
    int normalizacion = 1;
    int centro_enemigo_x = int(enemigo.posicion.x + trunc(enemigo.dimension.longitud_x / 2)), centro_enemigo_y = int(enemigo.posicion.y + trunc(enemigo.dimension.longitud_y / 2));
    int centro_personaje_x = int(principal.posicion.x + trunc(principal.dimension.longitud_x / 2)), centro_personaje_y = int(principal.posicion.y + trunc(principal.dimension.longitud_y / 2));
    int dx_enemigo = centro_personaje_x < centro_enemigo_x ? -1 : centro_personaje_x == centro_enemigo_x ? 0 : 1;
    int dy_enemigo = centro_personaje_y < centro_enemigo_y ? -1 : centro_personaje_y == centro_enemigo_y ? 0 : 1;
    if (dx_enemigo && dy_enemigo) normalizacion = sqrt(2);
    if (!activado) enemigo.estado.direccion = dx_enemigo >= 0 ? 0 : 1;
    enemigo.posicion.x += (enemigo.velocidad.rapidez * dx_enemigo / normalizacion) / FRAMES;
    enemigo.posicion.y += (enemigo.velocidad.rapidez * dy_enemigo / normalizacion) / FRAMES;
    if (int(pos_x_guardada) != int(enemigo.posicion.x) || int(pos_y_guardada) != int(enemigo.posicion.y)) enemigo.estado.verificar_movimiento = true;
    enemigo.guardado_posicion.x = pos_x_guardada;
    enemigo.guardado_posicion.y = pos_y_guardada;
}
void movimiento_todos_enemigos(Lugar& lugar) {
    for (int i = 0; i < lugar.atributos.enemigos_cantidad; i++) movimiento_enemigo(lugar.atributos.enemigos[i], lugar.personaje, lugar.atributos.tutores[2].poder_activado);
    for (int i = 0; i < lugar.atributos.balas_cantidad; i++) movimiento_bala(lugar.atributos.balas[i]);
}
void movimiento_jugador(Lugar& lugar, bool& musica, bool& salir) {
    float pos_x_guardada = lugar.personaje.posicion.x, pos_y_guardada = lugar.personaje.posicion.y;
    int guardado_direccion = lugar.personaje.estado.direccion;
    if (_kbhit()) {
        char tecla = _getch();
        if (tecla == ARRIBA && lugar.personaje.posicion.y > 7) lugar.personaje.posicion.y -= lugar.personaje.velocidad.rapidez / FRAMES;
        else if (tecla == ABAJO && lugar.personaje.posicion.y + lugar.personaje.dimension.longitud_y < HEIGHT - 7) lugar.personaje.posicion.y += lugar.personaje.velocidad.rapidez / FRAMES;
        else if (tecla == IZQUIERDA && lugar.personaje.posicion.x >= 1) {
            lugar.personaje.posicion.x -= lugar.personaje.velocidad.rapidez / FRAMES;
            lugar.personaje.estado.direccion = 1;
        }
        else if (tecla == DERECHA && lugar.personaje.posicion.x + lugar.personaje.dimension.longitud_x <= WIDTH - 23) {
            lugar.personaje.posicion.x += lugar.personaje.velocidad.rapidez / FRAMES;
            lugar.personaje.estado.direccion = 0;
        }
        else if (tecla == ESPACIO && ((lugar.personaje.posicion.x + lugar.personaje.dimension.longitud_x - 1 < WIDTH - 4 && lugar.personaje.estado.direccion == 0) || (lugar.personaje.posicion.x - 3 > 2 && lugar.personaje.estado.direccion == 1))) {
            if (lugar.atributos.balas_cantidad < lugar.atributos.balas_total) lanzar_bala(lugar);
        }
        else if ((tecla == TECLA_1) && lugar.atributos.tutores[0].tutor_activado) {
            lugar.atributos.tutores[0].tutor_activado = false;
            lugar.atributos.balas_total = 3;
            lugar.atributos.tutores[0].poder_activado = true;
            for (int i = 0; i < 4; i++) lugar.atributos.tutores[0].cara_tutor[i] = 12;
        }
        else if ((tecla == TECLA_2) && lugar.atributos.tutores[1].tutor_activado) {
            lugar.atributos.tutores[1].tutor_activado = false;
            lugar.personaje.estado.vida = 20;
            lugar.atributos.tutores[1].poder_activado = true;
            for (int i = 0; i < 4; i++) lugar.atributos.tutores[1].cara_tutor[i] = 10;
            dibujar_lineas(RojoO, 14, 3, 40, 1);
        }
        else if ((tecla == TECLA_3) && lugar.atributos.tutores[2].tutor_activado) {
            lugar.atributos.tutores[2].tutor_activado = false;
            lugar.atributos.tutores[2].poder_activado = true;
            for (int i = 0; i < 4; i++) lugar.atributos.tutores[2].cara_tutor[i] = 9;
            for (int i = 0; i < lugar.atributos.enemigos_cantidad; i++) lugar.atributos.enemigos[i].velocidad.rapidez = 0;
        }
        else if (tecla == ESC) {
            salir = true;
        }
        else if (tecla == 77 || tecla == 109) {
            if (musica) {
                PlaySound(0, 0, 0);
                musica = false;
            }
            else {
                PlaySound(TEXT(".\\musica\\fondo.wav"), NULL, SND_FILENAME | SND_LOOP | SND_ASYNC);
                musica = true;
            }
        }
    }
    if (int(pos_x_guardada) != int(lugar.personaje.posicion.x) || int(pos_y_guardada) != int(lugar.personaje.posicion.y)) lugar.personaje.estado.verificar_movimiento = true;
    lugar.personaje.guardado_posicion.x = pos_x_guardada;
    lugar.personaje.guardado_posicion.y = pos_y_guardada;
}

