void ordenar_personas_burbuja(Persona* grupo_personas, int numero) {
    for (int i = 0; i < numero - 1; ++i) {
        for (int j = 0; j < numero - i - 1; ++j) {
            if (grupo_personas[j].puntaje < grupo_personas[j + 1].puntaje) {
                // Intercambiar las personas
                Persona temp = grupo_personas[j];
                grupo_personas[j] = grupo_personas[j + 1];
                grupo_personas[j + 1] = temp;
            }
        }
    }
    for (int i = 0; i < numero; ++i) {
        if (i == 0) {
            grupo_personas[i].puesto = 1;
        }
        else if (grupo_personas[i].puntaje == grupo_personas[i - 1].puntaje) {
            grupo_personas[i].puesto = grupo_personas[i - 1].puesto;
        }
        else {
            grupo_personas[i].puesto = i + 1;
        }
    }
}

Persona* mencionar_ranking(Persona* grupo_personas, Persona jugador, int& numero) {
    Persona* personas_finalistas = new Persona[numero + 1];
    for (int i = 0; i < numero; i++) personas_finalistas[i] = grupo_personas[i];
    personas_finalistas[numero] = jugador;
    ordenar_personas_burbuja(personas_finalistas, numero + 1);
    Persona* top3 = new Persona[3];
    for (int i = 0; i < 3; i++) top3[i] = personas_finalistas[i];
    delete[] personas_finalistas;
    numero = 3;
    return top3;
}

