void reiniciar_valor(Lugar& lugar, Lugar* eliminar) {
    for (int i = 0; i < 3; i++) {
        lugar.atributos.contenedores[i].atributos.items_ahora = 0;
        lugar.atributos.contenedores[i].atributos.color_letra = Negro;
    }
    
    delete eliminar;

}

void victoria(Lugar& lugar, int tiempo, int puntaje, bool you_win, Persona nuevo_jugador) {
    if (you_win) {
        PlaySound(TEXT(".\\musica\\victory.wav"), NULL, SND_FILENAME | SND_ASYNC);
        nuevo_jugador.puntaje = tiempo + puntaje;
        dibujar_fondo_matriz(lugar.fondo.matriz[1]);
        lugar.ranking_lugar.personas = mencionar_ranking(lugar.ranking_lugar.personas, nuevo_jugador, lugar.ranking_lugar.numero);
        funcion_continuar();
        PlaySound(0, 0, 0);
        dibujar_ranking(lugar.ranking_lugar.personas, lugar);
        funcion_salir();
    }
}

void dar_puntaje(Lugar lugar, bool& you_win, int& puntaje_actual, int& tiempo, int& contador_frames) {
    you_win = true;
    int puntaje = 0, numero_actual, numero_solicitado;
    for (int i = 0; i < 3; i++) {
        numero_actual = lugar.atributos.contenedores[i].atributos.items_ahora;
        numero_solicitado = lugar.atributos.contenedores[i].atributos.items_requerido;
        if (numero_actual >= numero_solicitado) puntaje += 250 - (numero_actual - numero_solicitado) * 10; // Si sobrepasa el numero, restara 10
    }
    puntaje_actual = puntaje;
    for (int i = 0; i < 3; i++) {
        if (lugar.atributos.contenedores[i].atributos.items_requerido > lugar.atributos.contenedores[i].atributos.items_ahora) you_win = false;
    }
}

