void jugar() {
    srand(time(NULL));
    int eleccion;
    string nombre_jugador = funcion_eleccion(eleccion);
    Persona nuevo_jugador = Persona(nombre_jugador, 0);
    Lugar* lugar_usar_puntero = new Lugar(lugares_juego[eleccion]);
    Lugar& lugar_usar = *lugar_usar_puntero;
    dibujar_inicio_mapa(lugar_usar);

    bool you_win = false, salir = false, you_lose_confirmado = false, musica_activada = true;
    int puntaje_actual = 0, tiempo = 999, contador_frames = 0;
    PlaySound(TEXT(".\\musica\\fondo.wav"), NULL, SND_FILENAME | SND_LOOP | SND_ASYNC);

    while (true) {
        inicializar_objetos_enemigos(lugar_usar);
        dibujar_nuevamente_informacion_mapa(lugar_usar, puntaje_actual, tiempo, contador_frames);
        accion_juego(lugar_usar, you_lose_confirmado, musica_activada, salir);
        dar_puntaje(lugar_usar, you_win, puntaje_actual, tiempo, contador_frames);
        if (tiempo <= 0) you_lose(lugar_usar, dibujo_enemigo_tiempo, 9, 10, you_lose_confirmado);
        if (you_win || you_lose_confirmado || salir) break;
    }
    victoria(lugares_juego[eleccion], tiempo, puntaje_actual, you_win, nuevo_jugador);
    reiniciar_valor(lugar_usar, lugar_usar_puntero);
    PlaySound(TEXT(".\\musica\\intro.wav"), NULL, SND_FILENAME | SND_ASYNC);
}

